import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const [notes, setNotes] = useState([{nTitle: "", nText: ""}]);

  function createNote(newNoteTitle, newNoteText) {

    const tempNote = {nTitle: newNoteTitle, nText: newNoteText};
    setNotes((prevValue) => {
      return [...prevValue, tempNote];
    })

  }

  return (
    <div>
      <Header />
      <CreateArea addNote={createNote}/>
      {console.log(notes)}
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
