import React, {useState} from "react";
import Header from "./Header";
import Footer from "./Footer";
import Note from "./Note";
import CreateArea from "./CreateArea";

function App() {

  const [notes, setNotes] = useState([{nTitle: "", nText: ""}]);

  function createNote(newNoteTitle, newNoteText) {
    setNotes((prevValue) => {
      return [...prevValue, {nTitle: newNoteTitle, nText: newNoteText}];
    })
    console.log(notes);

  }

  return (
    <div>
      <Header />
      <CreateArea addNote={createNote}/>
      <Note key={1} title="Note title" content="Note content" />
      <Footer />
    </div>
  );
}

export default App;
