import React, {useState} from "react";

function CreateArea(props) {
  const [inputTitle, setInputTitle] = useState("");
  const [inputText, setInputText] = useState("");

  function handleInput(event) {
    setInputTitle(event.target.value);
    // console.log(event.target.value);
  }

  function handleText(event) {
    setInputText(event.target.value);
    // console.log(event.target.value);
  }

  return (
    <div>
      <form>
        <input onChange={handleInput} name="title" placeholder="Title" value={inputTitle}/>
        <textarea onChange={handleText} name="content" placeholder="Take a note..." rows="3" value={inputText}/>
        <button onClick={() => {props.addNote(inputTitle, inputText)}}>Add</button>
        {console.log(inputText, inputTitle)}
      </form>
    </div>
  );
}

export default CreateArea;
