import React, {useState} from "react";
import AddCircleIcon from '@mui/icons-material/AddCircle';

function CreateArea(props) {

  const [note, setNote] = useState({
    title: "",
    content: ""
  })

  function handleText(event) {
    const {name, value} = event.target;

    // if (name === "title") {
    //   setNote({title: value, content: note.content});
    // } else if (name === "content") {
    //   setNote({title: note.title, content: value})
    // }

    setNote((prevValue) => {
      return {
        ...prevValue,
        [name]: value
      }
    });
  }

  return (
    <div>
      <form className="create-note">
        <input onChange={handleText} name="title" placeholder="Title" value={note.title}/>
        <textarea onChange={handleText} name="content" placeholder="Take a note..." rows="3" value={note.content}/>
        <button onClick={(event) => {
          props.onAdd(note);
          event.preventDefault();
          setNote({title: "", content: ""});
        }}>Add</button>

      </form>
    </div>
  );
}

export default CreateArea;
